note_models.css:
.card {
 font-family: Arial;
 font-size: 20px;
 text-align: left;
 color: black;
 background-color: white;
}

.cloze {
 font-weight: bold;
 color: blue;
}

.nightMode .cloze {
 color: lightblue;
}

.section-header {
 font-weight: bold;
 margin-top: 15px;
 margin-bottom: 5px;
}

.original-text, .improved-text {
 margin: 10px 0;
 padding: 10px;
 background-color: #f5f5f5;
 border-radius: 5px;
}

.feedback {
 margin-top: 15px;
 padding: 10px;
 background-color: #e8f4f8;
 border-radius: 5px;
}
